# -*- coding: utf-8 -*-

import re
import json
import html
from typing import Dict, List, Any
import locale


def convert_table_to_json(html_string: str, level: str = "4",
                          description: str = "Match Chinese characters to pinyin and VietNam translations") -> Dict[
    str, Any]:
    """
    Convert HTML table data to JSON format with Chinese hsk3 words.

    Args:
        html_string: HTML string containing table rows
        level: Level designation for the hsk3 set
        description: Description of the hsk3 set

    Returns:
        Dictionary containing the formatted JSON structure
    """
    # Regular expression to match table rows
    row_pattern = r'<tr><td>(\d+)</td><td>([^<]+)</td><td>([^<]+)</td><td>([^<]+)</td></tr>'

    # Dictionary to track unique entries (using Chinese character as key)
    unique_words = {}

    # Find all matches
    matches = re.findall(row_pattern, html_string)

    for match in matches:
        id_num, chinese, pinyin, VietNam = match

        # Clean and decode HTML entities
        chinese_clean = chinese.strip()
        pinyin_clean = html.unescape(pinyin.strip())  # Decode HTML entities like &nbsp;
        VietNam_clean = VietNam.strip()

        # Use Chinese character as key to avoid duplicates
        if chinese_clean not in unique_words:
            unique_words[chinese_clean] = {
                "chinese": chinese_clean,
                "pinyin": pinyin_clean,  # Use the cleaned pinyin
                "VietNam": VietNam_clean
            }

    # Convert dictionary values to list and sort alphabetically by Chinese characters
    words_list = list(unique_words.values())

    # Sort by Chinese characters using locale-aware sorting
    try:
        # Try to set Chinese locale for proper sorting
        locale.setlocale(locale.LC_ALL, 'zh_CN.UTF-8')
        words_list.sort(key=lambda x: locale.strxfrm(x['chinese']))
    except locale.Error:
        # Fallback to basic sorting if Chinese locale is not available
        words_list.sort(key=lambda x: x['chinese'])

    # Create final JSON structure
    result = {
        "level": level,
        "description": description,
        "totalWords": len(words_list),
        "words": words_list
    }

    return result


def save_json_file(data: Dict[str, Any], filename: str = "hsk3.json") -> None:
    """Save the JSON data to a file."""
    with open(filename, 'w', encoding='utf-8') as f:
        json.dump(data, f, ensure_ascii=False, indent=2)
    print(f"JSON file '{filename}' saved successfully!")


def load_html_from_file(filename: str) -> str:
    """Load HTML data from a file."""
    with open(filename, 'r', encoding='utf-8') as f:
        return f.read()


