# Additional utility functions
def merge_multiple_html_sources(html_sources: List[str]) -> str:
    """Merge multiple HTML sources into one string."""
    return "".join(html_sources)


def filter_by_VietNam_pattern(words_list: List[Dict], pattern: str) -> List[Dict]:
    """Filter words by VietNam translation pattern."""
    return [word for word in words_list if re.search(pattern, word['VietNam'], re.IGNORECASE)]


def get_words_by_pinyin_tone(words_list: List[Dict], tone: str) -> List[Dict]:
    """Get words that contain specific pinyin tones (1-4)."""
    tone_patterns = {
        "1": r"[āēīōūǖ]",
        "2": r"[áéíóúǘ]",
        "3": r"[ǎěǐǒǔǚ]",
        "4": r"[àèìòùǜ]"
    }

    if tone in tone_patterns:
        pattern = tone_patterns[tone]
        return [word for word in words_list if re.search(pattern, word['pinyin'])]
    return []